import React, { useState, useEffect } from "react";
import socket from "./socket";

function App() {
  const [username, setUsername] = useState("");
  const [room, setRoom] = useState("");
  const [message, setMessage] = useState("");
  const [messages, setMessages] = useState([]);

  useEffect(() => {
    socket.on("sendMessage", ({ room, user, message }) => {
      console.log(`ðŸ“© Message received in ${room}: ${user} -> ${message}`);  // Debug log
      io.to(room).emit("message", { user, text: message });
    });
    

    return () => socket.off("message");
  }, []);

  const joinRoom = () => {
    if (username && room) {
      socket.emit("joinRoom", { username, room });
    }
  };

  const sendMessage = () => {
    if (message) {
      socket.emit("sendMessage", { room, user: username, message });
      setMessage("");
    }
  };

  return (
    <div style={{ padding: "20px", textAlign: "center" }}>
      <h2>Real-Time Chat</h2>
      {!room ? (
        <div>
          <input type="text" placeholder="Enter Username" value={username} onChange={(e) => setUsername(e.target.value)} />
          <input type="text" placeholder="Enter Room Name" value={room} onChange={(e) => setRoom(e.target.value)} />
          <button onClick={joinRoom}>Join Room</button>
        </div>
      ) : (
        <div>
          <h3>Room: {room}</h3>
          <div style={{ border: "1px solid #ccc", padding: "10px", height: "200px", overflowY: "scroll", marginBottom: "10px" }}>
            {messages.map((msg, index) => (
              <p key={index}>
                <strong>{msg.user}: </strong> {msg.text}
              </p>
            ))}
          </div>
          <input type="text" placeholder="Type a message..." value={message} onChange={(e) => setMessage(e.target.value)} />
          <button onClick={sendMessage}>Send</button>
        </div>
      )}
    </div>
  );
}

export default App;
